import React from 'react';
import logo from './logo.svg';
import './App.css';
import Header from "./components/Header";
import Products from "./components/Products";

class App extends React.Component{
  handle_onClick(){
    alert("HAHAHA");
  }
  render(){
    var products = [
      {
        id : 1,
        name : "Hai Sang"
      },
      {
        id : 2,
        name : "Van Thanh"
      },
      {
        id : 3,
        name : "Si Liem"
      }
    ];

    let elements = products.map((product, index) => {
      return <Products key={product.id} name={product.name}></Products>
    });

    return(
      <div>
        <Header />
        <div className="container">
          <div className="row">
            <div className="row">
              <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
                {elements}
              </div>
              <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
                <button type="button" className="btn btn-warning" onClick={this.handle_onClick}>Click Me</button>
              </div>
            </div>
          </div>
        </div>

      </div>
    );
  }
}

export default App;




