import React from 'react';

class TagForm extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            name : '',
            status : false
        }
    }

    onCloseForm = () =>{
        this.props.onCloseForm();
    }

    onChangeForm = (event) => {
        var target = event.target;
        var name = target.name;
        var value = target.value;
        if(name === 'txtStatus'){
            value = target.value === 'true' ? true : false;
        }
        this.setState({
            [name] : value
        });
    }

    onSubmitForm = (event) =>{
        event.preventDefault();
        this.props.PropsSubmit(this.state);
        this.props.onCancel();
    }

    BtnClear = () => {
        this.setState({
            name : '',
            status : false
        });
    }

    BtnCancel = () => {
        this.props.onCancel();
    }

    render() {

        return (
                <div className="panel panel-warning">
                    <div className="panel-heading">
                        <h3 className="panel-title">Thêm Công Việc
                            <span onClick={this.onCloseForm} className="fa fa-times-circle text-right float-right"></span>
                        </h3>
                        
                    </div>
                    <div className="panel-body">
                        <form onSubmit={this.onSubmitForm}>
                            <div className="form-group">
                                <label>Tên :</label>
                                <input type="text" className="form-control" name="name" value={this.state.name} onChange={this.onChangeForm} />
                            </div>
                            <label>Trạng Thái :</label>
                            <select className="form-control" name="status" required="required" value={this.state.statuss} onChange={this.onChangeForm}>
                                <option value={true}>Kích Hoạt</option>
                                <option value={false}>Ẩn</option>
                            </select>
                            <br/>
                            <div className="text-center">
                                <button type="submit" className="btn btn-warning">Thêm</button>&nbsp;
                                <button type="button" onClick={this.BtnClear} className="btn btn-danger">Hủy Bỏ</button>
                            </div>
                        </form>
                    </div>
                </div>
        );
    }
}

export default TagForm;




