import React from 'react';
import logo from './logo.svg';
import './App.css';
import Header from "./components/Header";
import Products from "./components/Products";

class App extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      products : [
        {
          id : 1,
          name : "Hai Sang"
        },
        {
          id : 2,
          name : "Van Thanh"
        },
        {
          id : 3,
          name : "Si Liem"
        }
      ],
      isActive : true
    };
    // this.isActived = this.isActived.bind(this);
  }

  isActived = () => {
    this.setState({
      isActive : !this.state.isActive
    });
  }

  render(){
    let elements = this.state.products.map((product, index) => {
      // return <Products key={product.id} name={product.name}></Products>
      return <tr>
              <th scope="row">{product.id}</th>
              <td>{product.name}</td>
            </tr>
    });

    return(
      <div>
        <Header />
        <div className="container">
          <div className="row">
            <div className="row">
              <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
                <table class="table">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">Name</th>
    </tr>
  </thead>
  <tbody>
    {elements}
  </tbody>
</table>
              </div>
              <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
    <button type="button" className="btn btn-warning" onClick={this.isActived}>Click Me {this.state.isActive ? "True":"False"}</button>
              </div>
            </div>
          </div>
        </div>

      </div>
    );
  }
}

export default App;




