import React from 'react';
import './App.css';
import TagForm from './components/TagForm';
import Search_Sort from './components/Search_Sort';
import TaskList from './components/TaskList';
var randomstring = require("randomstring");

class App extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            Tasks : [],
            isDisplayForm : true
        }
    }

    componentDidMount(){
        if(localStorage && localStorage.getItem("tasks")){
            var task = JSON.parse(localStorage.getItem('tasks'));
            this.setState({
                Tasks : task
            })
        }
    }

    onGeneratorData = () => {
        var Tasks = [
            {
                id:randomstring.generate({
                    length : 7,
                    charset : 'numeric'
                }),
                name:"Học Lập Trình",
                status : true
            },
            {
                id:randomstring.generate({
                    length : 7,
                    charset : 'numeric'
                }),
                name:"Đi Bơi",
                status : false
            },
            {
                id:randomstring.generate({
                    length : 7,
                    charset : 'numeric'
                }),
                name:"Đi Ngủ ",
                status : true
            }
        ];
        this.setState({
            Tasks : Tasks
        });
        localStorage.setItem('tasks',JSON.stringify(Tasks));
    }
    // LocalStorage : setItem('key', value); getItem('key'); removeItem('key');
    render(){
        var {Tasks, isDisplayForm} = this.state;
        var elmTaskForm = isDisplayForm === true ? <TagForm /> : "";
    return(
    <div className="container">
        <div className="text-center">
            <h1>Quản Lý Công Việc</h1>
            <hr/>
        </div>
        <div className="row">
            <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4">
                {elmTaskForm}
            </div>
            <div className="col-xs-8 col-sm-8 col-md-8 col-lg-8">
                <button type="button" className="btn btn-primary">
                    <span className="fa fa-plus mr-5"></span>Thêm Công Việc
                </button>
                <button onClick={this.onGeneratorData} type="button" className="btn btn-danger">
                    Generator Data
                </button>
                <div className="row mt-15">
                    <Search_Sort />
                </div>
                <div className="row mt-15">
                    <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
                        <TaskList tasks={Tasks} />
                    </div>
                </div>
            </div>
        </div>
    </div>
    );
  }
}

export default App;




