import React from 'react';

class TagForm extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            txtTask : '',
            txtStatus : false
        }
    }

    onCloseForm = () =>{
        this.props.onCloseForm();
    }

    onChangeForm = (event) => {
        // var target = event.target;
        // var name = target.name;
        // var value = target.value;
        // this.setState({
        //     [name] : value
        // });
    }

    onSubmitForm = (event) =>{
        event.preventDefault();
        console.log(this.state);
    }

    render() {

        return (
                <div className="panel panel-warning">
                    <div className="panel-heading">
                        <h3 className="panel-title">Thêm Công Việc
                            <span onClick={this.onCloseForm} className="fa fa-times-circle text-right float-right"></span>
                        </h3>
                        
                    </div>
                    <div className="panel-body">
                        <form onSubmit={this.onSubmitForm}>
                            <div className="form-group">
                                <label>Tên :</label>
                                <input type="text" className="form-control" name="txtTask"  value={this.state.txtTask} />
                            </div>
                            <label>Trạng Thái :</label>
                            <select className="form-control" name="txtStatus" required="required" onChange={this.onChangeForm} value={this.state.txtStatus}>
                                <option value="1">Kích Hoạt</option>
                                <option value="0">Ẩn</option>
                            </select>
                            <br/>
                            <div className="text-center">
                                <button type="submit" className="btn btn-warning">Thêm</button>&nbsp;
                                <button type="submit" className="btn btn-danger">Hủy Bỏ</button>
                            </div>
                        </form>
                    </div>
                </div>
        );
    }
}

export default TagForm;




